	public TwitterAPIModelDecoder(String aLoginToken) {
		// TODO Auto-generated constructor stub
		this.loginToken = aLoginToken;
	}

	@Override
	public boolean canDecodeSource(String aSourceName) {
		// TODO Auto-generated method stub
		return aSourceName.startsWith("http://132.145.208.127:8080/tweet/"); 
	}

	@Override
	public ILcdModel decode(String aSource) throws IOException {
		// TODO Auto-generated method stub

		System.out.println("Aqui");

		TLcdDataObjectIndexedAnd2DBoundsIndexedModel model = new TLcdDataObjectIndexedAnd2DBoundsIndexedModel();

		model.setModelReference(new TLcdGeodeticReference());
		model.setModelDescriptor(new TwitterModelDescriptor(aSource));

		// We create an idnex on our model over the ID property. This will allow us to
		// efficiently retrive a vehicle based
		// on its ID. This is needed to update the poisition of the vehicles.
		//IndexId indexId = model.addIndex(Collections.singletonList(TWITTER_QTD), true);

		// Retrieve the JSON response from the API
		String response = apiGETData("coronavirus");

		System.out.println(response);

		Collection<Tweet> positions = new TwitterPositionsParser().unmarshall(response);
		positions.forEach(v -> model.addElement(v, ILcdModel.NO_EVENT));
		// Schedule a task to update the positions, on a background thread.
		// executor.scheduleAtFixedRate( new UpdatePositions( model, indexId ), 5, 5,
		// TimeUnit.SECONDS );

		return model;
	}

